mapper.update:

update teamhonor
set
  honor_title = #{honorTitle,jdbcType=VARCHAR}
  honor_picture = #{honorPicture,jdbcType=VARCHAR},
  honor_time = #{honorTime,jdbcType=TIMESTAMP},
  honor_type = #{honorType,jdbcType=VARCHAR},
  honor_timesstamp = #{honorTimesstamp,jdbcType=INTEGER},
  honor_introduce = #{honorIntroduce,jdbcType=LONGVARCHAR},
  honor_note = #{honorNote,jdbcType=LONGVARCHAR}
where honor_sid = #{honorSid,jdbcType=INTEGER}

mapper.select:
select honor_sid, honor_title,honor_picture, honor_time, honor_type, honor_timesstamp, honor_introduce,
  honor_note
from teamhonor ORDER BY honor_sid LIMIT #{0},#{1};

select honor_sid, honor_title, honor_picture, honor_time, honor_type, honor_timesstamp, honor_introduce,
honor_note
from teamhonor ORDER BY honor_sid DESC LIMIT #{0};
